package com.voicesofwynn.core.loadmanager;

import com.voicesofwynn.core.utils.VOWByteReader;

import java.io.FileWriter;
import java.nio.ByteBuffer;
import java.util.Map;

public interface RegisterType {
    /**
     * @return generated object (if needed by parent) or null.
     */
    Object load(VOWByteReader bytes);

    /**
     *
     * @param writer file writer in to which to write the generated bytes
     * @param section section is the loaded yaml part that this type owns
     * @param parent parent of section
     */
    void write(FileWriter writer, Object section, Map<String, Object> parent);

    String getName();
}
